using System.Collections.Generic;
using System.Diagnostics;

namespace _2015;

public static class DirectionResolver
{
    public static int Resolve(string input)
    {
        int floorCounter = 0;
        for (int i = 0; i < input.Length; i++)
        {
            char c = input[i];
            if (c == '(')
            {
                floorCounter++;
            } 
            else if (c == ')')
            {
                floorCounter--;
            }
            else 
            {
                Debug.Fail($"Invalid character {c}");
            }
        }

        return floorCounter;
    }

    public static int FindFloor(string input, int toFind)
    {
        int floorCounter = 0;
        for (int i = 0; i < input.Length; i++)
        {
            char c = input[i];
            if (c == '(')
            {
                floorCounter++;
            } 
            else if (c == ')')
            {
                floorCounter--;
            }
            else 
            {
                Debug.Fail($"Invalid character {c}");
            }

            if (floorCounter == toFind)
            {
                return i + 1;
            }
        }

        return -1;
    }
}

