    [TestMethod]
    public void TestInputs()
    {
        Dictionary<string, int> testInput = new Dictionary<string, int>()
        {
            { "(())", 0 },
            { "()()", 0 },
            { "(((", 3 },
            { "(()(()(", 3},
            { "))(((((" , 3},
            { "())", -1 },
            { "))(", -1 },
            { ")))", -3 },
            { ")())())", -3 },
        };

        foreach (var kvp in testInput)
        {
            int numFloors = DirectionResolver.Resolve(kvp.Key);
            Assert.AreEqual(kvp.Value, DirectionResolver.Resolve(kvp.Key), $"Expected {kvp.Key} to have {kvp.Value} floors. Actual: {numFloors}");
        }
    }

